import React, { useState, useEffect } from "react"
import AzureCustomerCards from "./AzureCustomerCards"
import './AzureCustomers.css'

function AzureCustomers() {
  const [nav, setNav] = useState([])
  const [data, setData] = useState([])

  const fetchData = () => {
    fetch(
      "https://raw.githubusercontent.com/Sreekanthm7/jsondata/main/azure-json-data/data.json"
    )
    .then((response) => {
        return response.json()
    })
    .then((data) => {
        setNav(data.azureCustomers)
        setData(data.azureCustomers)
    })
  }

  useEffect(() => {
    fetchData()
  },[])
  console.log(nav);
  return (
    <>
      <div className="azure-customer-container">
          <div className="customer-icons-container">

            {nav.map((item) => {
              return (
                <a>
                <div className="customer-img-container"> 
                    <img src={item.image} />
                </div>
              </a>
              )
            })}

          </div>
          <div>
            {data.map((item) => {
              return(
                <AzureCustomerCards 
                  img ={item.backgroundImg}
                  text = {item.text}
                />

              )
            })}

          </div>
      </div>
    </>
  )
}

export default AzureCustomers
