import React, { useState, useEffect } from "react"
import "./HybridData.css"

function HybridData({img,text}) {
  const [data, setData] = useState([])
  const fetchData = () => {
    fetch(
      "https://raw.githubusercontent.com/Sreekanthm7/jsondata/main/azure-json-data/data.json"
    )
      .then((responce) => {
        return responce.json()
      })
      .then((data) => {
        setData(data.hybridData)
      })
  }

  useEffect(() => {
    fetchData()
  }, [])
  console.log(data)
  return (
    <>
      {data.map((item) => {
        return (
          <>
            <div className="hybrid-data-container">
              <div className="hybrid-data">
                <div className="hybrid-cards-container">
                  <div className="hybrid-cards">
                    <img src={item.backgroundImg} />
                    <div className="hybrid-content">
                      <h3>{item.title}</h3>
                      <p>{item.text}</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </>
        )
      })}
    </>
  )
}

export default HybridData
